import React, { useState } from 'react';

const AddLinkModal = ({ addLinkToAPI }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [product, setProduct] = useState('');
  const [form, setForm] = useState('');

  const handleAddLink = (e) => {
    e.preventDefault();

    const newLink = {
      name: name,
      description: description,
      product: product,
      form: form
    };

    addLinkToAPI(newLink);

    // Close the modal after adding the link
    document.getElementById('add-link-modal').click();
  };

  return (
    <div className="modal fade" id="add-link-modal" tabIndex="-1" role="dialog" aria-labelledby="add-link-modal-label" aria-hidden="true">
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="add-link-modal-label">Add Link</h5>
            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <form onSubmit={handleAddLink}>
              <div className="form-group">
                <label htmlFor="name">Name</label>
                <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea className="form-control" id="description" value={description} onChange={(e) => setDescription(e.target.value)} required />
              </div>
              <div className="form-group">
                <label htmlFor="product">Product</label>
                <input type="text" className="form-control" id="product" value={product} onChange={(e) => setProduct(e.target.value)} required />
              </div>
              <div className="form-group">
                <label htmlFor="form">Form</label>
                <input type="text" className="form-control" id="form" value={form} onChange={(e) => setForm(e.target.value)} required />
              </div>
              <button type="submit" className="btn btn-primary">Add</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddLinkModal;
