import React, { useState, useEffect } from 'react';
import Link from './Link';

const LinksList = () => {
  const [links, setLinks] = useState([]);

  useEffect(() => {
    fetchLinksData();
  }, []);

  // Function to fetch data from the API and populate the table
  const fetchLinksData = () => {
    fetch('https://64bf88840d8e251fd110f977.mockapi.io/links', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to fetch data from the API.');
      })
      .then(linksData => {
        setLinks(linksData);
      })
      .catch(error => {
        console.error(error);
      });
  };

  // Function to add a new link to the API
  const addLinkToAPI = (linkData) => {
    fetch('https://64bf88840d8e251fd110f977.mockapi.io/links', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(linkData)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to add data to the API.');
      })
      .then(newLink => {
        // Call fetchLinksData to refresh the table with the updated data
        fetchLinksData();
      })
      .catch(error => {
        console.error(error);
      });
  };
  return (
    <div className="container">
      <h1 className="mt-4 mb-4">Links List</h1>

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Product</th>
            <th>Form</th>
          </tr>
          <tr>
            <td>Name1</td>
            <td>Description1</td>
            <td>Product1</td>
            <td>Form1</td>
          </tr>
          <tr>
            <td>Name2</td>
            <td>Description2</td>
            <td>Product2</td>
            <td>Form2</td>
          </tr>
          <tr>
            <td>Name3</td>
            <td>Description3</td>
            <td>Product3</td>
            <td>Form3</td>
          </tr>
          <tr>
            <td>Name4</td>
            <td>Description4</td>
            <td>Product4</td>
            <td>Form4</td>
          </tr>
        </thead>
        <tbody id="links-table">
          {links.map((link, index) => (
            <Link key={index} link={link} />
          ))}
        </tbody>
      </table>

      <div className="text-center mb-4">
        <button
          className="btn btn-primary"
          data-toggle="modal"
          data-target="#add-link-modal"
        >
          + Add Link
        </button>
      </div>
    </div>
  );
};
