import React, { useState } from 'react';
import { FiMenu } from 'react-icons/fi';
import { Link, animateScroll as scroll } from 'react-scroll';
import logo from './Qburst-Logo.png';
import '../App.css';

const MenuBar = () => {
  const [showDropdowns, setShowDropdowns] = useState(false);

  const toggleDropdowns = () => {
    setShowDropdowns(!showDropdowns);
  };

  return (
    <header>
      <nav>
        <div className="logo">
          <img src={logo} alt="Company Logo" />
        </div>
        {/* Hamburger Menu Icon */}
        <div className="menu-icon" onClick={toggleDropdowns}>
          <FiMenu />
        </div>
        {/* Desktop Menu */}
        <ul className={`menu-list ${showDropdowns ? 'show-dropdowns' : ''}`}>
          <li>
            <Link
              to="home"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleDropdowns}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="about"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleDropdowns}
            >
              About
            </Link>
          </li>
          <li>
            <Link
              to="contact"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleDropdowns}
            >
              Contact
            </Link>
          </li>
        </ul>
        {/* Mobile Dropdown Menu */}
        <ul className={`dropdown-menu ${showDropdowns ? 'show-dropdowns' : ''}`}>
          <li>
            <Link
              to="home"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleDropdowns}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="about"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleDropdowns}
            >
              About
            </Link>
          </li>
          <li>
            <Link
              to="contact"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
              onClick={toggleDropdowns}
            >
              Contact
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default MenuBar;
