import React from 'react';

const AddLinkModal = ({ addLink }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    const name = e.target.name.value;
    const description = e.target.description.value;
    const product = e.target.product.value;
    const form = e.target.form.value;

    const newLink = {
      name: name,
      description: description,
      product: product,
      form: form,
    };

    // Call the addLink function passed from the parent component to add the new link
    addLink(newLink);

    // Reset the form fields
    e.target.reset();
  };

  return (
    <div className="modal" id="add-link-modal">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Add Link</h5>
            <button type="button" className="close" data-dismiss="modal">
              &times;
            </button>
          </div>
          <div className="modal-body">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" className="form-control" required />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description:</label>
                <input
                  type="text"
                  id="description"
                  name="description"
                  className="form-control"
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="product">Product:</label>
                <select id="product" name="product" className="form-control" required>
                  <option value="Product A">Product A</option>
                  <option value="Product B">Product B</option>
                  <option value="Product C">Product C</option>
                  <option value="Product D">Product D</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="form">Form:</label>
                <select id="form" name="form" className="form-control" required>
                  <option value="Form X">Form X</option>
                  <option value="Form Y">Form Y</option>
                  <option value="Form Z">Form Z</option>
                  <option value="Form W">Form W</option>
                </select>
              </div>
              <div className="text-center">
                <button type="submit" className="btn btn-primary">
                  Add
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddLinkModal;
