import React, { useState, useEffect } from 'react';
import Link from './Link';

const LinksList = () => {
  const [links, setLinks] = useState([]);
  const [editLink, setEditLink] = useState(null);

  useEffect(() => {
    fetchLinksData();
  }, []);

  const fetchLinksData = () => {
    fetch('https://64bf88840d8e251fd110f977.mockapi.io/links', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to fetch data from the API.');
      })
      .then(linksData => {
        setLinks(linksData);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const addLinkToAPI = (linkData) => {
    fetch('https://64bf88840d8e251fd110f977.mockapi.io/links', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(linkData)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to add data to the API.');
      })
      .then(newLink => {
        fetchLinksData(); // Call fetchLinksData to refresh the table with the updated data
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateLinkInAPI = (linkId, updatedLinkData) => {
    fetch(`https://64bf88840d8e251fd110f977.mockapi.io/links/${linkId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedLinkData)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to update data in the API.');
      })
      .then(updatedLink => {
        fetchLinksData(); // Call fetchLinksData to refresh the table with the updated data
        setEditLink(null); // Clear the editLink state after successful update
      })
      .catch(error => {
        console.error(error);
      });
  };

  const deleteLinkFromAPI = (linkId) => {
    fetch(`https://64bf88840d8e251fd110f977.mockapi.io/links/${linkId}`, {
      method: 'DELETE'
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to delete data from the API.');
      })
      .then(deletedLink => {
        fetchLinksData(); // Call fetchLinksData to refresh the table after successful deletion
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleAddLink = (e) => {
    e.preventDefault();

    const name = e.target.elements.name.value;
    const description = e.target.elements.description.value;
    const product = e.target.elements.product.value;
    const form = e.target.elements.form.value;

    const newLink = {
      name: name,
      description: description,
      product: product,
      form: form
    };

    // Add the new link to the API and update the table
    addLinkToAPI(newLink);

    // Hide the modal (if this code is related to a Bootstrap modal)
  };

  const handleEditLink = (e) => {
    e.preventDefault();

    const linkId = e.target.elements.id.value;
    const name = e.target.elements.name.value;
    const description = e.target.elements.description.value;
    const product = e.target.elements.product.value;
    const form = e.target.elements.form.value;

    const updatedLink = {
      name: name,
      description: description,
      product: product,
      form: form
    };

    // Update the link in the API and update the table
    updateLinkInAPI(linkId, updatedLink);
  };

  const handleEditBtnClick = (link) => {
    setEditLink(link); // Set the link to be edited
  };

  const handleDeleteBtnClick = (linkId) => {
    // Implement the delete functionality here
    // For example, you can show a confirmation modal before deleting the link
    // After confirmation, call the deleteLinkFromAPI() function passing the linkId to delete the link
    console.log('Delete link with ID:', linkId);
    deleteLinkFromAPI(linkId);
  };

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">Links List</h1>

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Product</th>
            <th>Form</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody id="links-table">
          {links.map((link, index) => (
            <tr key={index}>
              <td>{link.name}</td>
              <td>{link.description}</td>
              <td>{link.product}</td>
              <td>{link.form}</td>
              <td>
                <button className="btn btn-info btn-sm" onClick={() => handleEditBtnClick(link)}>Edit</button>
                <button className="btn btn-danger btn-sm" onClick={() => handleDeleteBtnClick(link.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="text-center mb-4">
        <button
          className="btn btn-primary"
          data-toggle="modal"
          data-target="#add-link-modal"
          id="add-link-modal"
        >
          + Add Link
        </button>
      </div>

      <div className="modal" id="add-link-modal">
        {/* ... Same as the provided HTML ... */}
      </div>

      {editLink && (
        <div className="modal" id="edit-link-modal">
          {/* ... Same as the provided HTML ... */}
        </div>
      )}
    </div>
  );
};

export default LinksList;
