import React, { useState, useEffect } from 'react';
// import Link from './Link';

const LinksList = () => {
  const [links, setLinks] = useState([]);
  const [editLink, setEditLink] = useState(null);
  const handleAddLinkButtonClick = () => {
    setShowAddLinkModal(true); // Show the "Add Link" modal
  };
  const [showAddLinkModal, setShowAddLinkModal] = useState(false);

  useEffect(() => {
    fetchLinksData();
  }, []);

  const fetchLinksData = () => {
    fetch('https://64bf88840d8e251fd110f977.mockapi.io/links', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to fetch data from the API.');
      })
      .then(linksData => {
        setLinks(linksData);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const addLinkToAPI = (linkData) => {
    fetch('https://64bf88840d8e251fd110f977.mockapi.io/links', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(linkData)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to add data to the API.');
      })
      .then(newLink => {
        fetchLinksData(); // Call fetchLinksData to refresh the table with the updated data
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateLinkInAPI = (linkId, updatedLinkData) => {
    fetch(`https://64bf88840d8e251fd110f977.mockapi.io/links/${linkId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedLinkData)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to update data in the API.');
      })
      .then(updatedLink => {
        fetchLinksData(); // Call fetchLinksData to refresh the table with the updated data
        setEditLink(null); // Clear the editLink state after successful update
      })
      .catch(error => {
        console.error(error);
      });
  };

  const deleteLinkFromAPI = (linkId) => {
    fetch(`https://64bf88840d8e251fd110f977.mockapi.io/links/${linkId}`, {
      method: 'DELETE'
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to delete data from the API.');
      })
      .then(deletedLink => {
        fetchLinksData(); // Call fetchLinksData to refresh the table after successful deletion
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleAddLink = (e) => {
    e.preventDefault();

    const name = e.target.elements.name.value;
    const description = e.target.elements.description.value;
    const product = e.target.elements.product.value;
    const form = e.target.elements.form.value;

    const newLink = {
      name: name,
      description: description,
      product: product,
      form: form
    };

    // Add the new link to the API and update the table
    addLinkToAPI(newLink);

    // Hide the modal
    setShowAddLinkModal(false);
  };

  // Event handler for editing an existing link
  const handleEditLink = (e) => {
    e.preventDefault();

    const linkId = e.target.elements.id.value;
    const name = e.target.elements.name.value;
    const description = e.target.elements.description.value;
    const product = e.target.elements.product.value;
    const form = e.target.elements.form.value;

    const updatedLink = {
      id: linkId,
      name: name,
      description: description,
      product: product,
      form: form
    };

    // Update the link in the API and update the table
    updateLinkInAPI(linkId, updatedLink);

    // Hide the modal
    setEditLink(null);
  };

  const handleDeleteBtnClick = (linkId) => {
    // Implement the delete functionality here
    // For example, you can show a confirmation modal before deleting the link
    // After confirmation, call the deleteLinkFromAPI() function passing the linkId to delete the link
    console.log('Delete link with ID:', linkId);
    deleteLinkFromAPI(linkId);
  };

  return (
    <div className="container">
      <h1 className="mt-4 mb-4">Links List</h1>

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Product</th>
            <th>Form</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody id="links-table">
          {links.map((link, index) => (
            <tr key={index}>
              <td>{link.name}</td>
              <td>{link.description}</td>
              <td>{link.product}</td>
              <td>{link.form}</td>
              <td>
                <button className="btn btn-info btn-sm" onClick={() => handleEditLink(link)}>Edit</button>
                <button className="btn btn-danger btn-sm" onClick={() => handleDeleteBtnClick(link.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="text-center mb-4">
        <button
          className="btn btn-primary"
          data-toggle="modal"
          data-target="#add-link-modal"
          id="add-link-modal"
          onClick={() => setShowAddLinkModal(true)}
        >
          + Add Link
        </button>
      </div>

      {showAddLinkModal && (
        <div className="modal" id="add-link-modal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Add Link</h5>
                <button type="button" className="close" data-dismiss="modal" onClick={() => setShowAddLinkModal(false)}>&times;</button>
              </div>
              <div className="modal-body">
                <form id="add-link-form" onSubmit={handleAddLink}>
                  <div className="form-group">
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" name="name" className="form-control" required />
                  </div>
                  <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <input type="text" id="description" name="description" className="form-control" required />
                  </div>
                  <div className="form-group">
                    <label htmlFor="product">Product:</label>
                    <select id="product" name="product" className="form-control" required>
                      <option value="Product A">Product A</option>
                      <option value="Product B">Product B</option>
                      <option value="Product C">Product C</option>
                      <option value="Product D">Product D</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label htmlFor="form">Form:</label>
                    <select id="form" name="form" className="form-control" required>
                      <option value="Form X">Form X</option>
                      <option value="Form Y">Form Y</option>
                      <option value="Form Z">Form Z</option>
                      <option value="Form W">Form W</option>
                    </select>
                  </div>
                  <div className="text-center">
                    <button type="submit" className="btn btn-primary">Add</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}

      {editLink && (
        <div className="modal" id="edit-link-modal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Link</h5>
                <button type="button" className="close" data-dismiss="modal" onClick={() => setEditLink(null)}>&times;</button>
              </div>
              <div className="modal-body">
                <form id="edit-link-form" onSubmit={handleEditLink}>
                  <input type="hidden" id="edit-link-id" name="id" value={editLink.id} />
                  <div className="form-group">
                    <label htmlFor="edit-link-name">Name:</label>
                    <input type="text" id="edit-link-name" name="name" className="form-control" required defaultValue={editLink.name} />
                  </div>
                  <div className="form-group">
                    <label htmlFor="edit-link-description">Description:</label>
                    <input type="text" id="edit-link-description" name="description" className="form-control" required defaultValue={editLink.description} />
                  </div>
                  <div className="form-group">
                    <label htmlFor="edit-link-product">Product:</label>
                    <select id="edit-link-product" name="product" className="form-control" required defaultValue={editLink.product}>
                      <option value="Product A">Product A</option>
                      <option value="Product B">Product B</option>
                      <option value="Product C">Product C</option>
                      <option value="Product D">Product D</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label htmlFor="edit-link-form">Form:</label>
                    <select id="edit-link-form" name="form" className="form-control" required defaultValue={editLink.form}>
                      <option value="Form X">Form X</option>
                      <option value="Form Y">Form Y</option>
                      <option value="Form Z">Form Z</option>
                      <option value="Form W">Form W</option>
                    </select>
                  </div>
                  <div className="text-center">
                    <button type="submit" className="btn btn-primary">Save Changes</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default LinksList;
