import React, { useState } from 'react';
import { FiMenu } from 'react-icons/fi';
import { GoogleLogin } from 'react-google-login';
import logo from './Qburst-Logo.png';

const MenuBar = () => {
  const [showDropdowns, setShowDropdowns] = useState(false);

  const toggleDropdowns = () => {
    setShowDropdowns(!showDropdowns);
  };

  const handleMenuItemClick = () => {
    setShowDropdowns(false);
  };

  const responseGoogle = (response) => {
    // Handle the Google authentication response here
    console.log(response);
  };

  return (
    <header className="bg-light py-3 shadow-sm">
      <div className="container">
        <nav className="d-flex justify-content-between align-items-center">
          <div className="logo">
            <img src={logo} alt="Company Logo" height="50" />
          </div>
          <div className="menu-icon" onClick={toggleDropdowns}>
            <FiMenu />
          </div>
          <ul className={`menu-list list-inline mb-0 ${showDropdowns ? 'd-block' : 'd-none'}`}>
            <li className="list-inline-item">
              <a href="#home" onClick={handleMenuItemClick}>
                Home
              </a>
            </li>
            <li className="list-inline-item">
              <a href="#about" onClick={handleMenuItemClick}>
                About
              </a>
            </li>
            <li className="list-inline-item">
              <a href="#contact" onClick={handleMenuItemClick}>
                Contact
              </a>
            </li>
          </ul>
          <div className="auth-container">
            {/* Add Google authentication button here */}
            <GoogleLogin
              clientId="YOUR_GOOGLE_CLIENT_ID"
              buttonText="Login with Google"
              onSuccess={responseGoogle}
              onFailure={responseGoogle}
              cookiePolicy={'single_host_origin'}
            />
          </div>
        </nav>
      </div>
    </header>
  );
};

export default MenuBar;
